func TestNew(t *testing.T) {
	tests := []struct {
		name string
		size int
		want int
	}{
		{
			name: "4 bits",
			size: 4,
			want: 1,
		},
		{
			name: "8 bits",
			size: 8,
			want: 1,
		},
		{
			name: "32 bits",
			size: 32,
			want: 1,
		},
		{
			name: "64 bits",
			size: 64,
			want: 1,
		},
		{
			name: "128 bits",
			size: 128,
			want: 2,
		},
		{
			name: "1024 bits",
			size: 1024,
			want: 16,
		},
		{
			name: "1048 bits",
			size: 1048,
			want: 17,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			b := New(tt.size)
			assert.NotNil(t, b)
			assert.Len(t, b.bits, tt.want)
		})
	}
}

func TestNew_Error(t *testing.T) {
	assert.PanicsWithValue(t,
		"invalid size",
		func() {
			New(0)
		},
	)
}

func TestNewFromString(t *testing.T) {
	tests := []struct {
		name string
		str  string
		want int
	}{
		{
			name: "4 bits",
			str:  "0000",
			want: 1,
		},
		{
			name: "8 bits",
			str:  "10101010",
			want: 1,
		},
		{
			name: "32 bits",
			str:  "10101010101010101010101010101010",
			want: 1,
		},
		{
			name: "64 bits",
			str:  "1010101010101010101010101010101010101010101010101010101010101010",
			want: 1,
		},
		{
			name: "128 bits",
			str:  "10101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010",
			want: 2,
		},
		{
			name: "1024 bits",
			str:  "",
			want: 16,
		},
		{
			name: "1048 bits",
			str:  "",
			want: 17,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			b := NewFromString(tt.str)
			assert.NotNil(t, b)
			assert.Len(t, b.bits, tt.want)
		})
	}
}

func TestNewFromString_Error(t *testing.T) {
	assert.PanicsWithValue(t,
		"unexpected binary string",
		func() {
			NewFromString("1012")
		},
	)
}

